package edu.in;

public class Pro1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean b = true;
		System.out.println("Boolean value:"+b);

		byte range; 
		range = 127;
		System.out.println("byte value:"+range);

        short range1;
		range1 = -127;
		System.out.println("Short value:"+range1);

		int range2 = 300000000; 
		System.out.println("int value :"+range2);

		long range3 = 30000000000L;
		System.out.println("Long value :"+range3);

		double number = 99.9;
		System.out.println("Double value:"+number);
		
         float number1 = 99.9f; 
		System.out.println(" Float value:"+number1);

		char letter = '\u0041';
		System.out.println("char value"+letter);
		
		char letter2 = 65;  
		System.out.println("char value:"+letter2);

		char letter3 = 'A';
		System.out.println("char value:"+letter3);

   }

}
